<script>
    'use strict';
    let slider = document.getElementById('sliderArea');
    let activeImageIndex = 0;

    
    let imageArray = slider.getElementsByTagName('img');
    imageArray[activeImageIndex].style.display = 'block';


    function nextImage() {
        imageArray[activeImageIndex].style.display = 'none';
        if (activeImageIndex >= imageArray.length - 1) {
            activeImageIndex = 0;
        } else {
            activeImageIndex += 1;
        }
        imageArray[activeImageIndex].style.display = 'block';
    }

    function prevImage() {
        imageArray[activeImageIndex].style.display = 'none';
        if (activeImageIndex == 0 ){
            activeImageIndex = imageArray.length -1;
        } else {
            activeImageIndex -= 1;
        }
        imageArray[activeImageIndex].style.display = 'block';
    }
</script>


